using RestaurantApp.BL.Dtos.Order;
using RestaurantApp.Core.Models;

namespace RestaurantApp.BL.Profiles
{
    public class OrderProfile
    {
        public static Order OrderCreateDtoToOrder(OrderCreateDto orderCreateDto)
        {
            if (orderCreateDto == null) return null;

            return new Order
            {
                OrderItems = orderCreateDto.OrderItems?.Select(oiDto => new OrderItem
                {
                    MenuItemId = oiDto.MenuItemId,
                    Count = oiDto.Count,
                }).ToList() ?? [],
                TableId = orderCreateDto.TableId,
            };
        }

        public static OrderListDto OrderToOrderListDto(Order order)
        {
            if (order == null) return null;

            var orderItemsListDtos = order.OrderItems?
                .Select(oi => OrderItemProfile.OrderItemToOrderItemListDto(oi))
                .ToList() ?? [];

            return new OrderListDto
            {
                Id = order.Id,
                TotalAmount = order.TotalAmount,
                Date = order.Date,
                OrderItems = orderItemsListDtos,
                TableId = order.TableId,
                TableNo = order.Table?.No,
                TableIsOccupied = order.Table.IsOccupied,
                TableOccupiedAt = order.Table?.OccupiedAt,
                TableReleasedAt = order.Table?.ReleasedAt
            };
        }

        public static List<OrderListDto> OrdersToOrderListDtos(IEnumerable<Order> orders)
        {
            if (orders == null) return [];
            return orders.Select(o => OrderToOrderListDto(o)).ToList();
        }
    }
}

using RestaurantApp.BL.Dtos.Order;
using RestaurantApp.Core.Models;

namespace RestaurantApp.BL.Profiles
{
    public class OrderProfile
    {
        public static Order OrderCreateDtoToOrder(OrderCreateDto orderCreateDto)
        {
            if (orderCreateDto == null) return null;

            return new Order
            {
                OrderItems = orderCreateDto.OrderItems?.Select(oiDto => new OrderItem
                {
                    MenuItemId = oiDto.MenuItemId,
                    Count = oiDto.Count,
                }).ToList() ?? []
            };
        }

        public static OrderListDto OrderToOrderListDto(Order order)
        {
            if (order == null) return null;

            var orderItemsListDtos = order.OrderItems?
                .Select(oi => OrderItemProfile.OrderItemToOrderItemListDto(oi))
                .ToList() ?? [];

            int menuItemCount = order.OrderItems?.Sum(oi => oi.Count) ?? 0;

            return new OrderListDto
            {
                Id = order.Id,
                TotalAmount = order.TotalAmount,
                Date = order.Date,
                MenuItemCount = menuItemCount,
                OrderItems = orderItemsListDtos
            };
        }

        public static List<OrderListDto> OrdersToOrderListDtos(IEnumerable<Order> orders)
        {
            if (orders == null) return [];
            return orders.Select(o => OrderToOrderListDto(o)).ToList();
        }
    }
}

using RestaurantApp.BL.Dtos.MenuItem;
using RestaurantApp.BL.Profiles; 
using RestaurantApp.BL.Services.Interfaces;
using RestaurantApp.Core.Enums;
using RestaurantApp.BL.Exceptions;
using RestaurantApp.Core.Models;
using RestaurantApp.DAL.Repositories.Concretes;
using RestaurantApp.DAL.Repositories.Interfaces;

namespace RestaurantApp.BL.Services.Concretes
{
    public class MenuItemService : IMenuItemService
    {
        private readonly IRepository<MenuItem> _menuItemRepository;
        public MenuItemService()
        {
            _menuItemRepository = new Repository<MenuItem>(); 
        }
        private List<MenuItemListDto> MapMenuItemsToDtoList(List<MenuItem> menuItems) => MenuItemProfile.MenuItemsToMenuItemListDtos(menuItems);

        public async Task AddMenuItemAsync(MenuItemCreateDto menuItemDto)
        {
            if (menuItemDto is null)
                throw new ArgumentNullException(nameof(menuItemDto), "Menu item data cannot be null.");

            var existingItem = await _menuItemRepository.GetAllAsync(q => q.Where(m => m.Name.ToLower() == menuItemDto.Name.ToLower()));
            if (existingItem.Any())
                throw new DuplicateMenuItemException($"A menu item with the name '{menuItemDto.Name}' already exists.");

            if (menuItemDto.Price <= 0)
                throw new InvalidMenuItemPriceException("Menu item price must be greater than zero.");

            if (menuItemDto.Quantity < 0)
                throw new InvalidMenuItemQuantityException("Menu item quantity must be a positive number.");

            if(menuItemDto.CostValue <= 0 && menuItemDto.CostValue < menuItemDto.Price)
                throw new InvalidMenuItemCostValueException("The menu item cost value must be greater than zero and less than the price.");

            var menuItem = MenuItemProfile.MenuItemCreateDtoToMenuItem(menuItemDto);
            await _menuItemRepository.CreateAsync(menuItem);
            await _menuItemRepository.SaveChangesAsync();
        }
        public async Task EditMenuItemAsync(MenuItemUpdateDto menuItemDto)
        {
            if (menuItemDto is null)
                throw new ArgumentNullException(nameof(menuItemDto), "Menu item update data cannot be null.");

            var menuItem = await _menuItemRepository.GetByIdAsync(menuItemDto.Id, useNoTracking:false) ?? 
                throw new MenuItemNotFoundException($"Menu item with ID {menuItemDto.Id} not found.");

            var duplicate = await _menuItemRepository.GetAllAsync(q => q.Where(t => t.Name.ToLower() == menuItemDto.Name.ToLower() && t.Id != menuItemDto.Id));
            if (duplicate.Any())
                throw new DuplicateMenuItemException($"A menu item with the name '{menuItemDto.Name}' already exists.");

            if (menuItemDto.Price <= 0)
                throw new InvalidMenuItemPriceException("Menu item price must be greater than zero.");

            if (menuItemDto.Quantity < 0)
                throw new InvalidMenuItemQuantityException("Menu item quantity must be a positive number.");

            if (menuItemDto.CostValue <= 0 && menuItemDto.CostValue < menuItemDto.Price)
                throw new InvalidMenuItemCostValueException("The menu item cost value must be greater than zero and less than the price.");

            MenuItemProfile.UpdateMenuItemFromDto(menuItem, menuItemDto);

            await _menuItemRepository.UpdateAsync(menuItem);
            await _menuItemRepository.SaveChangesAsync();
        }
        public async Task<List<MenuItemListDto>> GetAllMenuItemsAsync()
        {
            var menuItems = await _menuItemRepository.GetAllAsync() ?? throw new MenuItemNotFoundException("Not found any menu item.");
            return MapMenuItemsToDtoList(menuItems.ToList());
        }

        public async Task<List<MenuItemListDto>> GetMenuItemsByCategoryAsync(CategoryEnum category)
        {
            var filteredMenuItems = await _menuItemRepository.GetAllAsync(q => q.Where(m => m.Category == category)) ?? throw new MenuItemNotFoundException("Not found any menu item in this category.");
            return MapMenuItemsToDtoList(filteredMenuItems.ToList());
        }
        public async Task<List<MenuItemListDto>> GetMenuItemsByPriceIntervalAsync(decimal minPrice, decimal maxPrice)
        {
            var filteredMenuItems = await _menuItemRepository.GetAllAsync(q => q.Where(m => m.Price >= minPrice && m.Price <= maxPrice)) ?? throw new MenuItemNotFoundException("Not found any menu item in this price interval.");
            return MapMenuItemsToDtoList(filteredMenuItems.ToList());
        }
        public async Task RemoveMenuItemAsync(int id)
        {
            var menuItem = await _menuItemRepository.GetByIdAsync(id, useNoTracking: false) ?? 
                throw new MenuItemNotFoundException($"Menu item with ID {id} not found.");
            await _menuItemRepository.DeleteAsync(id);
            await _menuItemRepository.SaveChangesAsync();
        }
        public async Task<List<MenuItemListDto>> SearchMenuItemsAsync(string searchValue)
        {
            if (string.IsNullOrWhiteSpace(searchValue))
                return await GetAllMenuItemsAsync();

            string lowerSearchValue = searchValue.ToLower();
            var filteredMenuItems = await _menuItemRepository.GetAllAsync(q =>
                q.Where(m => m.Name.ToLower().Contains(lowerSearchValue) ||
                             m.Category.ToString().ToLower().Contains(lowerSearchValue)));

            return MapMenuItemsToDtoList(filteredMenuItems.ToList());
        }
        public async Task<MenuItemListDto> GetMenuItemByIdAsync(int id)
        {
            var menuItem = await _menuItemRepository.GetByIdAsync(id,useNoTracking:false);
            return MenuItemProfile.MenuItemToMenuItemListDto(menuItem);
        }
        public async Task<bool> DecreaseMenuItemQuantity(int menuItemId, int count)
        {
            var menuItem = await _menuItemRepository.GetByIdAsync(menuItemId, useNoTracking: false) ?? 
                throw new MenuItemNotFoundException($"Menu item with ID {menuItemId} not found.");
            if (menuItem.Quantity < count)
                return false;

            menuItem.Quantity -= count;
            await _menuItemRepository.UpdateAsync(menuItem);
            await _menuItemRepository.SaveChangesAsync();
            return true;
        }
        public async Task<bool> IncreaseMenuItemQuantity(int menuItemId, int count)
        {
            var menuItem = await _menuItemRepository.GetByIdAsync(menuItemId,useNoTracking:false) ??
                throw new MenuItemNotFoundException($"Menu item with ID {menuItemId} not found.");

            menuItem.Quantity += count;
            await _menuItemRepository.UpdateAsync(menuItem);
            await _menuItemRepository.SaveChangesAsync();
            return true;
        }
    }
}

using RestaurantApp.BL.Dtos.MenuItem;
using RestaurantApp.BL.Profiles; 
using RestaurantApp.BL.Services.Interfaces;
using RestaurantApp.Core.Enums;
using RestaurantApp.BL.Exceptions;
using RestaurantApp.Core.Models;
using RestaurantApp.DAL.Repositories.Concretes;
using RestaurantApp.DAL.Repositories.Interfaces;

namespace RestaurantApp.BL.Services.Concretes
{
    public class MenuItemService : IMenuItemService
    {
        private readonly IRepository<MenuItem> _menuItemRepository;
        public MenuItemService()
        {
            _menuItemRepository = new Repository<MenuItem>(); 
        }
        private List<MenuItemListDto> MapMenuItemsToDtoList(List<MenuItem> menuItems) => MenuItemProfile.MenuItemsToMenuItemListDtos(menuItems);
        
        public async Task AddMenuItemAsync(MenuItemCreateDto menuItemDto)
        {
            if (menuItemDto is null)
                throw new ArgumentNullException(nameof(menuItemDto), "Menu item data cannot be null.");

            var existingItems = await _menuItemRepository.GetAllAsync();
            if (existingItems.Any(m => m.Name.ToLower() == menuItemDto.Name.ToLower()))
                throw new DuplicateMenuItemException($"A menu item with the name '{menuItemDto.Name}' already exists.");

            if (menuItemDto.Price <= 0)
                throw new InvalidMenuItemPriceException("Menu item price must be greater than zero.");

            var menuItem = MenuItemProfile.MenuItemCreateDtoToMenuItem(menuItemDto);
            await _menuItemRepository.CreateAsync(menuItem);
            await _menuItemRepository.SaveChangesAsync();
        }

        public async Task EditMenuItemAsync(MenuItemUpdateDto menuItemDto)
        {
            if (menuItemDto is null)
                throw new ArgumentNullException(nameof(menuItemDto), "Menu item update data cannot be null.");

            var menuItem = await _menuItemRepository.GetByIdAsync(menuItemDto.Id) ?? 
                throw new MenuItemNotFoundException($"Menu item with ID {menuItemDto.Id} not found.");

            var allItems = await _menuItemRepository.GetAllAsync();
            if (allItems.Any(t => t.Name.ToLower() == menuItemDto.Name.ToLower() && t.Id != menuItemDto.Id))
                throw new DuplicateMenuItemException($"A menu item with the name '{menuItemDto.Name}' already exists.");

            if (menuItemDto.Price <= 0)
                throw new InvalidMenuItemPriceException("Menu item price must be greater than zero.");

            MenuItemProfile.UpdateMenuItemFromDto(menuItem, menuItemDto);

            await _menuItemRepository.UpdateAsync(menuItem);
            await _menuItemRepository.SaveChangesAsync();
        }

        public async Task<List<MenuItemListDto>> GetAllMenuItemsAsync()
        {
            var menuItems = await _menuItemRepository.GetAllAsync();
            if (!menuItems.Any())
                throw new MenuItemNotFoundException("Not found any menu item.");
            return MapMenuItemsToDtoList(menuItems);
        }

        public async Task<List<MenuItemListDto>> GetMenuItemsByCategoryAsync(CategoryEnum category)
        {
            var allItems = await _menuItemRepository.GetAllAsync();
            var filteredMenuItems = allItems.Where(m => m.Category == category).ToList();
            if (!filteredMenuItems.Any())
                throw new MenuItemNotFoundException("Not found any menu item in this category.");
            return MapMenuItemsToDtoList(filteredMenuItems);
        }

        public async Task<List<MenuItemListDto>> GetMenuItemsByPriceIntervalAsync(decimal minPrice, decimal maxPrice)
        {
            var allItems = await _menuItemRepository.GetAllAsync();
            var filteredMenuItems = allItems.Where(m => m.Price >= minPrice && m.Price <= maxPrice).ToList();
            if (!filteredMenuItems.Any())
                throw new MenuItemNotFoundException("Not found any menu item in this price interval.");
            return MapMenuItemsToDtoList(filteredMenuItems);
        }

        public async Task RemoveMenuItemAsync(int id)
        {
            var menuItem = await _menuItemRepository.GetByIdAsync(id) ?? 
                throw new MenuItemNotFoundException($"Menu item with ID {id} not found.");
            await _menuItemRepository.DeleteAsync(id);
            await _menuItemRepository.SaveChangesAsync();
        }

        public async Task<List<MenuItemListDto>> SearchMenuItemsAsync(string searchValue)
        {
            if (string.IsNullOrWhiteSpace(searchValue))
                return await GetAllMenuItemsAsync();

            string lowerSearchValue = searchValue.ToLower();
            var allItems = await _menuItemRepository.GetAllAsync();
            var filteredMenuItems = allItems.Where(m => 
                m.Name.ToLower().Contains(lowerSearchValue) ||
                m.Category.ToString().ToLower().Contains(lowerSearchValue)).ToList();

            return MapMenuItemsToDtoList(filteredMenuItems);
        }

        public async Task<MenuItemListDto> GetMenuItemByIdAsync(int id)
        {
            var menuItem = await _menuItemRepository.GetByIdAsync(id) ?? 
                throw new MenuItemNotFoundException($"Menu item with ID {id} not found.");
            return MenuItemProfile.MenuItemToMenuItemListDto(menuItem);
        }
    }
}

using Microsoft.EntityFrameworkCore; 
using RestaurantApp.BL.Dtos.Order;
using RestaurantApp.BL.Profiles; 
using RestaurantApp.BL.Services.Interfaces;
using RestaurantApp.BL.Exceptions;
using RestaurantApp.Core.Models;
using RestaurantApp.DAL.Repositories.Concretes;
using RestaurantApp.DAL.Repositories.Interfaces;

namespace RestaurantApp.BL.Services.Concretes
{
    public class OrderService : IOrderService
    {
        private readonly IRepository<Order> _orderRepository;
        private readonly IMenuItemService _menuItemService;
        private readonly ITableService _tableService;
        public OrderService(IMenuItemService menuItemService, ITableService tableService)
        {
            _orderRepository = new Repository<Order>();
            _menuItemService = menuItemService;
            _tableService = tableService; 
        }
        private OrderListDto MapOrderToDto(Order order) => OrderProfile.OrderToOrderListDto(order);
        private List<OrderListDto> MapOrdersToDtoList(IEnumerable<Order> orders) => OrderProfile.OrdersToOrderListDtos(orders);
        public async Task AddOrderAsync(OrderCreateDto orderDto)
        {
            if (orderDto is null)
                throw new ArgumentNullException(nameof(orderDto), "Order data cannot be null.");
            if (!orderDto.OrderItems.Any())
                throw new InvalidOrderDataException("An order must contain at least one item.");

            Order order = new Order
            {
                Date = DateTime.Now, 
                OrderItems = [],
                TableId = orderDto.TableId
			};

            decimal calculatedTotalAmount = 0;

            foreach (var itemDto in orderDto.OrderItems)
            {
                var menuItem = await _menuItemService.GetMenuItemByIdAsync(itemDto.MenuItemId) ??
                    throw new MenuItemNotFoundException($"Menu item with ID {itemDto.MenuItemId} not found for order item.");
                if (itemDto.Count <= 0)
                    throw new InvalidOrderDataException($"Count for menu item {menuItem.Name} must be positive.");

                bool decreased = await _menuItemService.DecreaseMenuItemQuantity(itemDto.MenuItemId, itemDto.Count);
                if (!decreased)
                {
                    throw new InvalidMenuItemQuantityException($"Not enough '{menuItem.Name}' in stock. Available: {menuItem.Quantity}. Requested: {itemDto.Count}.");
                }

                order.OrderItems.Add(new OrderItem
                {
                    MenuItemId = itemDto.MenuItemId,
                    Count = itemDto.Count,
                });
                calculatedTotalAmount += menuItem.Price * itemDto.Count;
            }

            order.TotalAmount = calculatedTotalAmount;
            await _tableService.OccupyTableAsync(order.TableId);
            await _orderRepository.CreateAsync(order);
            await _orderRepository.SaveChangesAsync(); 
        }
        public async Task RemoveOrderAsync(int orderId)
        {
            Order order = await _orderRepository.GetByIdAsync(orderId,
                                                              q => q.Include(t => t.Table)
                                                                    .Include(o => o.OrderItems)
                                                                        .ThenInclude(oi => oi.MenuItem),
                                                              false) 
                        ?? throw new OrderNotFoundException($"Order with ID {orderId} not found.");
            foreach (var orderItem in order.OrderItems)
            {
                await _menuItemService.IncreaseMenuItemQuantity(orderItem.MenuItemId, orderItem.Count);
            }

            await _tableService.ReleaseTableAsync(order.TableId);
            await _orderRepository.DeleteAsync(orderId);
            await _orderRepository.SaveChangesAsync();
        }
        public async Task<List<OrderListDto>> GetOrdersByDatesIntervalAsync(DateTime startDate, DateTime endDate)
        {
            List<Order> orders = await _orderRepository.GetAllAsync(q => q.Include(t => t.Table).Include(o => o.OrderItems).ThenInclude(oi => oi.MenuItem).Where(o => o.Date.Date >= startDate.Date && o.Date.Date <= endDate.Date)) ?? throw new OrderNotFoundException($"Order not found in this interval.");

            return MapOrdersToDtoList(orders);
        }
        public async Task<List<OrderListDto>> GetOrdersByDateAsync(DateTime date)
        {
            List<Order> orders = await _orderRepository.GetAllAsync(q =>q.Include(t => t.Table).Include(o => o.OrderItems).ThenInclude(oi => oi.MenuItem).Where(o => o.Date.Date == date.Date)) ?? throw new OrderNotFoundException($"Order not found.");

            return MapOrdersToDtoList(orders);
        }
        public async Task<List<OrderListDto>> GetOrdersByPriceIntervalAsync(decimal minPrice, decimal maxPrice)
        {
            List<Order> orders = await _orderRepository.GetAllAsync(q => q.Include(t => t.Table).Include(o => o.OrderItems).ThenInclude(oi => oi.MenuItem).Where(o => o.TotalAmount >= minPrice && o.TotalAmount <= maxPrice)) ?? throw new OrderNotFoundException($"Order not found in this interval.");

            return MapOrdersToDtoList(orders);
        }
        public async Task<List<OrderListDto>> GetAllOrdersAsync()
        {
            List<Order> orders = await _orderRepository.GetAllAsync(q =>q.Include(o => o.Table) 
                                                                         .Include(o => o.OrderItems)
                                                                         .ThenInclude(oi => oi.MenuItem)) ?? throw new OrderNotFoundException($"Order not found.");
            return MapOrdersToDtoList(orders.ToList());
        }
        public async Task<OrderListDto> GetOrderByNoAsync(int orderId)
        {
            Order order = await _orderRepository.GetByIdAsync(orderId, q => q.Include(t=>t.Table).Include(o => o.OrderItems).ThenInclude(oi => oi.MenuItem), useNoTracking:false) ?? throw new OrderNotFoundException($"Order with ID {orderId} not found.");
            return MapOrderToDto(order);
        }

        public async Task CompleteOrderAsync(int orderId)
        {
            var order = await _orderRepository.GetByIdAsync(orderId, useNoTracking: false) ?? 
                throw new OrderNotFoundException($"Order with ID {orderId} not found.");

            await _tableService.ReleaseTableAsync(order.TableId, order.TotalAmount); 
		}
    }
}

using Microsoft.EntityFrameworkCore; 
using RestaurantApp.BL.Dtos.Order;
using RestaurantApp.BL.Profiles; 
using RestaurantApp.BL.Services.Interfaces;
using RestaurantApp.BL.Exceptions;
using RestaurantApp.Core.Models;
using RestaurantApp.DAL.Repositories.Concretes;
using RestaurantApp.DAL.Repositories.Interfaces;

namespace RestaurantApp.BL.Services.Concretes
{
    public class OrderService : IOrderService
    {
        private readonly IRepository<Order> _orderRepository;
        private readonly IMenuItemService _menuItemService;
        
        public OrderService(IMenuItemService menuItemService)
        {
            _orderRepository = new Repository<Order>();
            _menuItemService = menuItemService;
        }
        
        private OrderListDto MapOrderToDto(Order order) => OrderProfile.OrderToOrderListDto(order);
        private List<OrderListDto> MapOrdersToDtoList(IEnumerable<Order> orders) => OrderProfile.OrdersToOrderListDtos(orders);
        
        public async Task AddOrderAsync(OrderCreateDto orderDto)
        {
            if (orderDto is null)
                throw new ArgumentNullException(nameof(orderDto), "Order data cannot be null.");
            if (!orderDto.OrderItems.Any())
                throw new InvalidOrderDataException("An order must contain at least one item.");

            Order order = new Order
            {
                Date = DateTime.Now, 
                OrderItems = []
            };

            decimal calculatedTotalAmount = 0;

            foreach (var itemDto in orderDto.OrderItems)
            {
                var menuItem = await _menuItemService.GetMenuItemByIdAsync(itemDto.MenuItemId) ??
                    throw new MenuItemNotFoundException($"Menu item with ID {itemDto.MenuItemId} not found for order item.");
                if (itemDto.Count <= 0)
                    throw new InvalidOrderDataException($"Count for menu item {menuItem.Name} must be positive.");

                order.OrderItems.Add(new OrderItem
                {
                    MenuItemId = itemDto.MenuItemId,
                    Count = itemDto.Count,
                });
                calculatedTotalAmount += menuItem.Price * itemDto.Count;
            }

            order.TotalAmount = calculatedTotalAmount;
            await _orderRepository.CreateAsync(order);
            await _orderRepository.SaveChangesAsync(); 
        }
        
        public async Task RemoveOrderAsync(int orderId)
        {
            var order = await _orderRepository.GetByIdAsync(orderId) ?? 
                throw new OrderNotFoundException($"Order with ID {orderId} not found.");

            await _orderRepository.DeleteAsync(orderId);
            await _orderRepository.SaveChangesAsync();
        }
        
        public async Task<List<OrderListDto>> GetOrdersByDatesIntervalAsync(DateTime startDate, DateTime endDate)
        {
            var allOrders = await _orderRepository.GetAllAsync();
            var filteredOrders = allOrders.Where(o => o.Date.Date >= startDate.Date && o.Date.Date <= endDate.Date).ToList();
            
            if (!filteredOrders.Any())
                throw new OrderNotFoundException($"Order not found in this interval.");

            return MapOrdersToDtoList(filteredOrders);
        }
        
        public async Task<List<OrderListDto>> GetOrdersByDateAsync(DateTime date)
        {
            var allOrders = await _orderRepository.GetAllAsync();
            var filteredOrders = allOrders.Where(o => o.Date.Date == date.Date).ToList();
            
            if (!filteredOrders.Any())
                throw new OrderNotFoundException($"Order not found.");

            return MapOrdersToDtoList(filteredOrders);
        }
        
        public async Task<List<OrderListDto>> GetOrdersByPriceIntervalAsync(decimal minPrice, decimal maxPrice)
        {
            var allOrders = await _orderRepository.GetAllAsync();
            var filteredOrders = allOrders.Where(o => o.TotalAmount >= minPrice && o.TotalAmount <= maxPrice).ToList();
            
            if (!filteredOrders.Any())
                throw new OrderNotFoundException($"Order not found in this interval.");

            return MapOrdersToDtoList(filteredOrders);
        }
        
        public async Task<List<OrderListDto>> GetAllOrdersAsync()
        {
            var orders = await _orderRepository.GetAllAsync();
            if (!orders.Any())
                throw new OrderNotFoundException($"Order not found.");
            return MapOrdersToDtoList(orders);
        }
        
        public async Task<OrderListDto> GetOrderByNoAsync(int orderId)
        {
            var order = await _orderRepository.GetByIdAsync(orderId) ?? 
                throw new OrderNotFoundException($"Order with ID {orderId} not found.");
            return MapOrderToDto(order);
        }
    }
}

