using Microsoft.EntityFrameworkCore;
using RestaurantApp.Core.Models.Common;
using RestaurantApp.DAL.Data;
using RestaurantApp.DAL.Repositories.Interfaces;

namespace RestaurantApp.DAL.Repositories.Concretes
{
    public class Repository<T> : IRepository<T> where T : BaseEntity
    {
        private readonly RestaurantDbContext _restaurantDbContext;
        public DbSet<T> Table { get; set; }
        public Repository()
        {
            _restaurantDbContext = new();
            Table = _restaurantDbContext.Set<T>();
        }
        public async Task CreateAsync(T entity) => await Table.AddAsync(entity);

        public async Task DeleteAsync(int id)
        {
            T entityToDelete = await GetByIdAsync(id, useNoTracking: false);
            if (entityToDelete != null)
                Table.Remove(entityToDelete);
        }

        public async Task<List<T>> GetAllAsync(Func<IQueryable<T>, IQueryable<T>> include = null)
        {
            IQueryable<T> query = Table;

            if (include != null)
                query = include(query);

            return await query.AsNoTracking().ToListAsync();
        }
        public async Task<T?> GetByIdAsync(int? id, Func<IQueryable<T>, IQueryable<T>> include = null, bool useNoTracking = true)
        {
            IQueryable<T> query = Table;

            if (include != null)
                query = include(query);

            if (useNoTracking)
                query = query.AsNoTracking();

            return await query.SingleOrDefaultAsync(m => m.Id == id);
        }
        public async Task SaveChangesAsync() => await _restaurantDbContext.SaveChangesAsync();

        public async Task UpdateAsync(T entity) 
        {
            T entityToUpdate = await GetByIdAsync(entity.Id,useNoTracking:false);
            if (entityToUpdate != null)
                Table.Update(entity);
        }
    }
}

using Microsoft.EntityFrameworkCore;
using RestaurantApp.Core.Models.Common;
using RestaurantApp.DAL.Data;
using RestaurantApp.DAL.Repositories.Interfaces;

namespace RestaurantApp.DAL.Repositories.Concretes
{
    public class Repository<T> : IRepository<T> where T : BaseEntity
    {
        private readonly RestaurantDbContext _restaurantDbContext;
        public DbSet<T> Table { get; set; }
        
        public Repository()
        {
            _restaurantDbContext = new();
            Table = _restaurantDbContext.Set<T>();
        }

        public async Task<T?> GetByIdAsync(int id)
        {
            return await Table.AsNoTracking().SingleOrDefaultAsync(m => m.Id == id);
        }

        public async Task<List<T>> GetAllAsync()
        {
            return await Table.AsNoTracking().ToListAsync();
        }

        public async Task CreateAsync(T entity)
        {
            await Table.AddAsync(entity);
        }

        public async Task UpdateAsync(T entity)
        {
            Table.Update(entity);
        }

        public async Task DeleteAsync(int id)
        {
            T? entityToDelete = await Table.FindAsync(id);
            if (entityToDelete != null)
                Table.Remove(entityToDelete);
        }

        public async Task SaveChangesAsync()
        {
            await _restaurantDbContext.SaveChangesAsync();
        }
    }
}

