        // MenuItem Operations
        private static async Task AddMenuItem()
        {
            Console.Clear();
            Console.WriteLine("=== YEN MENU TEM LAV ET ===");

            Console.Write("Ad: ");
            var name = Console.ReadLine() ?? "";

            Console.Write("Qiymti: ");
            if (!decimal.TryParse(Console.ReadLine(), out decimal price))
            {
                Console.WriteLine("Yanl qiymt format!");
                return;
            }

            Console.WriteLine("Kateqoriyalar:");
            var categories = Enum.GetValues<CategoryEnum>();
            for (int i = 0; i < categories.Length; i++)
            {
                Console.WriteLine($"{i + 1}. {categories[i]}");
            }

            Console.Write("Kateqoriya sein (nmr): ");
            if (!int.TryParse(Console.ReadLine(), out int categoryChoice) || 
                categoryChoice < 1 || categoryChoice > categories.Length)
            {
                Console.WriteLine("Yanl kateqoriya seimi!");
                return;
            }

            var dto = new MenuItemCreateDto
            {
                Name = name,
                Price = price,
                Category = categories[categoryChoice - 1]
            };

            await _menuItemService!.AddMenuItemAsync(dto);
            Console.WriteLine("Menu item uurla lav edildi!");
        }

        private static async Task EditMenuItem()
        {
            Console.Clear();
            Console.WriteLine("=== MENU TEMN DZL ET ===");

            Console.Write("Dzli edilck menu item ID-si: ");
            if (!int.TryParse(Console.ReadLine(), out int id))
            {
                Console.WriteLine("Yanl ID format!");
                return;
            }

            var existingItem = await _menuItemService!.GetMenuItemByIdAsync(id);
            Console.WriteLine($"Mvcud mlumatlar - Ad: {existingItem.Name}, Qiymt: {existingItem.Price}, Kateqoriya: {existingItem.Category}");

            Console.Write("Yeni ad: ");
            var name = Console.ReadLine() ?? "";

            Console.Write("Yeni qiymt: ");
            if (!decimal.TryParse(Console.ReadLine(), out decimal price))
            {
                Console.WriteLine("Yanl qiymt format!");
                return;
            }

            Console.WriteLine("Kateqoriyalar:");
            var categories = Enum.GetValues<CategoryEnum>();
            for (int i = 0; i < categories.Length; i++)
            {
                Console.WriteLine($"{i + 1}. {categories[i]}");
            }

            Console.Write("Yeni kateqoriya sein (nmr): ");
            if (!int.TryParse(Console.ReadLine(), out int categoryChoice) || 
                categoryChoice < 1 || categoryChoice > categories.Length)
            {
                Console.WriteLine("Yanl kateqoriya seimi!");
                return;
            }

            var dto = new MenuItemUpdateDto
            {
                Id = id,
                Name = name,
                Price = price,
                Category = categories[categoryChoice - 1]
            };

            await _menuItemService!.EditMenuItemAsync(dto);
            Console.WriteLine("Menu item uurla yenilndi!");
        }

