        // Order Operations
        private static async Task AddOrder()
        {
            Console.Clear();
            Console.WriteLine("=== YEN SFAR LAV ET ===");

            var orderItems = new List<OrderItemCreateDto>();

            while (true)
            {
                Console.WriteLine("\nMvcud menu itemlr:");
                var menuItems = await _menuItemService!.GetAllMenuItemsAsync();
                DisplayMenuItems(menuItems);

                Console.Write("\nMenu item ID-si (0 - bitir): ");
                if (!int.TryParse(Console.ReadLine(), out int menuItemId))
                {
                    Console.WriteLine("Yanl ID format!");
                    continue;
                }

                if (menuItemId == 0) break;

                Console.Write("Say: ");
                if (!int.TryParse(Console.ReadLine(), out int count) || count <= 0)
                {
                    Console.WriteLine("Yanl say format!");
                    continue;
                }

                orderItems.Add(new OrderItemCreateDto
                {
                    MenuItemId = menuItemId,
                    Count = count
                });

                Console.WriteLine("Item lav edildi!");
            }

            if (!orderItems.Any())
            {
                Console.WriteLine("Sifari n he bir item lav edilmdi!");
                return;
            }

            var orderDto = new OrderCreateDto
            {
                OrderItems = orderItems
            };

            await _orderService!.AddOrderAsync(orderDto);
            Console.WriteLine("Sifari uurla lav edildi!");
        }

        private static async Task RemoveOrder()
        {
            Console.Clear();
            Console.WriteLine("=== SFAR LV ET ===");

            Console.Write("Lv edilck sifari ID-si: ");
            if (!int.TryParse(Console.ReadLine(), out int orderId))
            {
                Console.WriteLine("Yanl ID format!");
                return;
            }

            await _orderService!.RemoveOrderAsync(orderId);
            Console.WriteLine("Sifari uurla lv edildi!");
        }

