        private static async Task ShowOrderById()
        {
            Console.Clear();
            Console.WriteLine("=== SFARN TRAFLI MLUMATLARI ===");

            Console.Write("Sifari ID-si: ");
            if (!int.TryParse(Console.ReadLine(), out int orderId))
